import { useEffect, useReducer } from "react";

interface AuthState {
  validando: boolean;
  token: string | null;
  username: string;
  nombre: string;
  action: 'login' | 'logout' | null;  // Nuevo campo para saber qué acción tomar
}

const initialState: AuthState = {
  validando: false,
  token: null,
  username: "",
  nombre: "",
  action: null,
};

type LoginPayload = {
  username: string;
  nombre: string;
}

type AuthAction = 
  | { type: 'logout' }
  | { type: 'login', payload: LoginPayload }
  | { type: 'startValidation', action: 'login' | 'logout' }; // Modificado para incluir la acción

function authReducer(state: AuthState, action: AuthAction): AuthState {
  switch (action.type) {
    case "logout":
      return {
        validando: false,
        token: null,
        username: "",
        nombre: "",
        action: null
      };
    
    case 'login':
      const {nombre, username} = action.payload;
      return {
        validando: false,
        token: 'xdxdxd',
        nombre,
        username,
        action: null
      };

    case 'startValidation':
      return {
        ...state,
        validando: true,
        action: action.action  // Guardar la acción en el estado
      };

    default:
      return state;
  }
}

const Login = () => {
  const [{ validando, token, nombre, action }, dispatch] = useReducer(authReducer, initialState);

  const login = () => {
    dispatch({ type: 'startValidation', action: 'login' });
  }

  const logout = () => {
    dispatch({ type: 'startValidation', action: 'logout' });
  }

  useEffect(() => {
    if (validando) {
      let timer: ReturnType<typeof setTimeout>;
      if (action === 'login') {
        timer = setTimeout(() => {
          dispatch({
            type: 'login',
            payload: {
              nombre: 'Dani',
              username: 'Drako'
            }
          });
        }, 1500);
      } else if (action === 'logout') {
        timer = setTimeout(() => {
          dispatch({ type: 'logout' });
        }, 1500);
      }
      return () => clearTimeout(timer);  // Limpiar el timer al desmontar o en cambios subsiguientes
    }
  }, [validando, action, dispatch]);

  if (validando) {
    return (
      <>
        <div className="alert alert-info">Validando...</div>
      </>
    );
  }

  return (
    <>
      <h3>Login</h3>

      {
        (token)
          ? <>
              <div className="alert alert-success">Autenticado como {nombre} </div>
              <button className="btn btn-danger" onClick={logout}>Logout</button>
            </>
          : 
            <>
              <div className="alert alert-danger">No autenticado</div>
              <button className="btn btn-primary" onClick={login}>Login</button>
            </>
      }
    </>
  );
};

export default Login;
