import { useState } from "react";


export const useForm = () => {
  const [formulario, setFormulario] = useState(formulario);

  const onChange = (value: string, campo: string) =>{
    setFormulario({
      ...formulario,
      [campo]: value
    });
  }

  return {
    ...formulario,
    formulario,
    onChange
  }
}
